package velocity_project;

public class forlp3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("start");
		//outer loop
		for(int z=1;z<4;z++)//no of repetition 3
		{
			//inner loop
			for(int x=1;x<5;x++)//no.of repetition 4
			{
				//no of repetition 12
				System.out.println("hi");//3*4=12
			}
		}

	}

}
