package velocity_project;

public class forlp4 {
//  3 nested for loop
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("start");
		//outter loop
		for(int z=1;z<3;z++)//repeat 2
		{
			//inner loop
			for (int x=1;x<4;x++)//repeat 3
			{
				for(int y=1;y<5;y++)//no of 4
				{
					//no. of repettion 2*3*4=24s
					System.out.println("hi");
				}
			}
		}

	}

}
