package velocity_project;

public class increamentop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		System.out.println(a);
		
	// post increament
		//1.printing
		//2.increment
		System.out.println(a++);//13
		//pre increament
		//1.increment
		//2.printing
		System.out.println(++a);
		a++;//a=a+1
		System.out.println(a);
		int b=23;
		int c=45;
		int x=b+c;
		
		

	}

}
