package velocity_project;

public class logicalop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true;
		boolean b=false;
		boolean c=a&&b;
		System.out.println(c);
		c=a||b;
		System.out.println(c);
		boolean x=false;
		c=!x;
		System.out.println(c);

	}

}
