package velocity_project;

public class star5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//outer lop=no of line
		for(int b=1;b<=5;b++)//3
		{
		//inner loop=no.of start line
		for(int a=0;a<b;a++)//5
			
		{
			System.out.print("*");
		}
		System.out.println();
	}

	}
}
