package velocity_project;

public class star7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//outer loop=no of line
		//for(int b=1;b<=5;b++)//3
		for(int b=5;b>=1;b--)
		{
			//inner loop=no of starts/line
			for(int a=0;a<b;a++)//5
			{
				System.out.print("*");
				
			}
			System.out.println();
		}

	}

}
